const styles = theme => ({
    container: {
        display: 'flex',
        flexWrap: 'wrap',
        '& label': {
            whiteSpace: 'nowrap',
            overflow: 'hidden',
            textOverflow: 'ellipsis',
            width: '100%',
            fontSize: 14
        }
    },
    menu: {
        width: 200,
    },
    btnContainer: {
        marginTop: 20,
        float: 'right'
    }
});

export default styles;
