import CustomHttpService from './custom-http.service';
import Config from '../config/config';
import CookieManagerService from './cookie.service';
import { AppConstant } from '../constants/app.constant';

import EncodeDecodeType from '../actiontype/encode-decode-type';
class UsageDetailsTabService {
    static fetchUsageDetailsTabApi() {
        const config = {
            headers: {
                'Content-Type': "application/x-www-form-urlencoded",
            }
        };
        const authDetails = CookieManagerService.getAuthDetails();
        const transactionId = CookieManagerService.getItem(AppConstant.CORRELATION_ID) || "";
        const channel = authDetails.channel || "";
        var url = Config.contextPath + "postpaid/siebel/card/planAndUsage?channel=" + channel + "&transactionId=" + transactionId;
        return CustomHttpService.get(url, config, EncodeDecodeType.DEFAULT);
    }
    static fetchUsageAddOnDetailsTabApi() {
        const config = {
            headers: {
                'Content-Type': "application/x-www-form-urlencoded",
            }
        };
        const transactionId = CookieManagerService.getItem(AppConstant.CORRELATION_ID) || "";
        var url = Config.contextPath + "postpaid/siebel/get-addons-info?transactionId=" + transactionId;
        return CustomHttpService.get(url, config, EncodeDecodeType.DEFAULT);
    }
    static checkIsUsageHoverType(usageDetailsTabApiResponse) {
        if (usageDetailsTabApiResponse && usageDetailsTabApiResponse.cardContent && usageDetailsTabApiResponse.cardContent.plan_and_usage && usageDetailsTabApiResponse.cardContent.plan_and_usage['ADD-ON']) {
            const usageAddOnDetailsDataObj = usageDetailsTabApiResponse.cardContent.plan_and_usage['ADD-ON'][0];
            return usageAddOnDetailsDataObj['usageHoverType'] === "ADDON";
        }
        return null;
    }

}

export default UsageDetailsTabService;
