import { SystemTypeConstant } from "../constants/system-type.constant";

class SystemTypeService {

    static isSiebelSystem(customerInfo) {
        const flag = customerInfo && customerInfo.systemName && customerInfo.systemName.toLowerCase() === SystemTypeConstant.SIEBEL_SYSTEM ? true : false;
        return flag;
    }

    static isLegacySystem(customerInfo) {
        const flag = customerInfo && customerInfo.systemName && customerInfo.systemName.toLowerCase() === SystemTypeConstant.LEGACY_SYSTEM ? true : false;
        return flag;
    }

    static isProspectSystem(customerInfo) {
        const flag = customerInfo && customerInfo.systemName && customerInfo.systemName.toLowerCase() === SystemTypeConstant.PROSPECT_SYSTEM ? true : false;
        return flag;
    }

    static isOtherSystem(customerInfo) {
        const flag = customerInfo && customerInfo.systemName && customerInfo.systemName.toLowerCase() === SystemTypeConstant.OTHER_SYSYTEM ? true : false;
        return flag;
    }
}

export default SystemTypeService;
