const BlueButtonsStyles = theme => ({
    button: {
        margin: 4,
        background: '#fff',
        color: '#03A9F4',
        minWidth: '150px',
        border: '1px solid #03A9F4',
        '&:hover': {
            background: '#fff',
            border: '1px solid #03A9F4'
        }
    },
    buttonHighlighted: {
        margin: 4,
        background: '#03A9F4',
        color: '#fff',
        minWidth: '150px',
        border: '1px solid #03A9F4',
        '&:hover': {
            background: '#fff',
            color: '#03A9F4',
            border: '1px solid #03A9F4'
        }
    },
    red:{
        color: 'white',
        background: 'red',
        border: '1px solid red',
        '&:hover': {
            background: '#fff',
            color: 'red',
            border: '1px solid red'
        }
    },
    green:{
        color: 'white',
        background: 'green',
        border: '1px solid green',
        '&:hover': {
            background: '#fff',
            color: 'green',
            border: '1px solid green'
        }
    },
    input: {
        display: 'none',
    },
});

export default BlueButtonsStyles
