import React from 'react';
import CircularProgress from '@material-ui/core/CircularProgress';

const style = {
  container: {
    position: 'fixed',
    width: '100%',
    height: '100%',
    background: 'rgba(0, 0, 0, 0.70)',
    zIndex: 9999,
    top: 0,
    left: 0
  },
  refresh: {
    width: '50px',
    height: '50px',
    position: 'absolute',
    margin: '0 auto',
    top: '40%',
    right: 0,
    left: 0,
    color: '#e40000'
  }
};
const AppLoader = (props) => {
  const { cardLoader } = props;

  return (
    <div>
      {cardLoader &&
        <div className="smallContainerLoader"><CircularProgress className="color-red" style={style.refresh} /></div>
      }
      {!cardLoader &&
        <div style={style.container}><CircularProgress className="color-red" style={style.refresh} /></div>
      }
    </div>
  );
}

export default AppLoader;
