import React, { Component } from 'react';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import { closeAppDialogRequest } from '../../../actions/app-dialog.action';
import { connect } from 'react-redux';
import { withStyles } from '@material-ui/core/styles';
import styles from './app-dialog.style';
import DFFInfo from '../../common/dff-info/dff-info.smart';
import { AppDialogConstant } from '../../../constants/app-dialog.constant';
import FTRMessageDialog from './ftr-message/ftr-message-dialog.smart';
import SRMessageDialog from './sr-message/sr-message-dialog.smart';
import DNDCategories from './dnd-categories/dnd-categories.smart';
import CreateFTR from './create-ftr/create-ftr.smart';
import ArcMessageDialog from './arc-issue/arc-message-dialog.smart';
import ClaimDFF from './claim-dff/claim-dff.smart';
import AuthMatrixDialog from './auth-matrix-dialog/auth-matrix-dialog.smart';
import PaymentHistoryDialog from './payment-details-card/payment-history-dialog.smart';
import ProvisioningConfirmationDialog from './provisioning-confirmation/provisioning-confirmation.smart';
import SRNotesDialog from './sr-notes/sr-notes-dialog.smart';
import SRTaskUpdate from '../../common/task-update/task-update.smart';
import TicketFilter from '../../uwq/ticket-filter/ticket-filter.smart';
import SRTaskUpdateCaseSelector from '../../common/task-update/task-update-case-selector.smart';

class AppDialog extends Component {

  closeDialogRequest = () => {
    this.props.closeAppDialogRequest();
  }

  dialogContentHandler() {
    const { dialogTitle, classes } = this.props;
    switch (dialogTitle) {
      case AppDialogConstant.DFF_DIALOG_TITLE:
        return <DFFInfo textFieldClass={classes.dffTextField} />
      case AppDialogConstant.FTR_DIALOG_TITLE:
        return <FTRMessageDialog />
      case AppDialogConstant.SR_DIALOG_TITLE:
        return <SRMessageDialog />
      case AppDialogConstant.DND_DIALOG_TITLE:
        return <DNDCategories />
      case AppDialogConstant.CREATE_FTR_TITLE:
        return <CreateFTR />
      case AppDialogConstant.ARC_DIALOG_TITLE:
        return <ArcMessageDialog />
      case AppDialogConstant.CLAIM_DFF_DIALOG_TITLE:
        return <ClaimDFF textFieldClass={classes.claimDFFTextField} />
      case AppDialogConstant.AUTH_MATRIX_DIALOG_TITLE:
        return <AuthMatrixDialog closeDialogRequest={this.closeDialogRequest} />
      case AppDialogConstant.PAYMENT_HISTORY_DIALOG_TITLE:
        return <PaymentHistoryDialog />
      case AppDialogConstant.PROV_CONFIRM_DIALOG_TITLE:
        return <ProvisioningConfirmationDialog closeDialogRequest={this.closeDialogRequest} />
      case AppDialogConstant.SR_NOTES_DETAILS_DIALOG_TITLE:
        return <SRNotesDialog closeDialogRequest={this.closeDialogRequest} />
      case AppDialogConstant.TASK_UPDATE_DIALOG_TITLE:
        return <SRTaskUpdate textFieldClass={classes.taskUpdateTextField} />
      case AppDialogConstant.UWQ_FILTER_DIALOG_TITLE:
        return <TicketFilter />
      case AppDialogConstant.TASK_UPDATE_CASE_SELECTOR_DIALOG_TITLE:
          return <SRTaskUpdateCaseSelector textFieldClass={classes.taskUpdateTextField} />
      default:
        return ''
    }
  }

  render() {
    const { openDialog, maxWidth, dialogTitle, classes } = this.props;

    let dialogTitleComponent = null
    if (dialogTitle !== AppDialogConstant.SR_DIALOG_TITLE) {
      dialogTitleComponent = <DialogTitle>
        <div className="position-relative">{dialogTitle}
          {dialogTitle !== AppDialogConstant.AUTH_MATRIX_DIALOG_TITLE && <div className="close-dialog" onClick={() => this.closeDialogRequest()}><span>Close</span></div>}
        </div>
      </DialogTitle>
    }
    return (
      <React.Fragment>
        <Dialog
          fullWidth={true}
          maxWidth={maxWidth}
          open={openDialog}
          classes={{ paperWidthXs: classes.paperWidthXs }}
        >

          {dialogTitle !== AppDialogConstant.SR_DIALOG_TITLE && <DialogTitle>
            <div className="position-relative">{dialogTitle}
              {dialogTitle !== AppDialogConstant.AUTH_MATRIX_DIALOG_TITLE && <div className="close-dialog" onClick={() => this.closeDialogRequest()}><span>Close</span></div>}
            </div>
          </DialogTitle>}

          {dialogTitle === AppDialogConstant.SR_DIALOG_TITLE && <DialogTitle>
            <div className="position-relative">.
              <div className="close-dialog" onClick={() => this.closeDialogRequest()}><span>Close</span></div>
            </div>
          </DialogTitle>}

          <DialogContent>
            {this.dialogContentHandler()}
          </DialogContent>
        </Dialog>
      </React.Fragment>
    )
  }
}

const mapStateToProps = state => {

  return {
    openDialog: state.appDialog.openDialog,
    dialogTitle: state.appDialog.dialogTitle,
    maxWidth: state.appDialog.maxWidth,
  };
};

const mapDispatchToProps = dispatch => ({
  closeAppDialogRequest: () => dispatch(closeAppDialogRequest())
});

export default connect(mapStateToProps, mapDispatchToProps)(withStyles(styles)(AppDialog));
