import { LocalStorageKeys } from '../constants/localstorage.enum';

export class LocalStorageService {

    static setRegistationInfo(value) {
        _setItem('registationInfo', value);
    }
    static getRegistrationInfo() {
        const registationInfo = _getItem(registationInfo);
        if (registationInfo != null && registationInfo !== undefined && registationInfo) {
            return registationInfo;
        } else {
            return null;
        }
    }
}

const _setItem = (key, value) => {
    localStorage.setItem(key, value);
}

const _getItem = (key) => {
    return localStorage.getItem(key);
}

function _removeItem(key) {
    localStorage.removeItem(key);
}
const _getPromiseItem = (key, value) => {
    return new Promise((resolve, reject) => {

        try {

            const value = _getItem(key);
            resolve(value);
        } catch (e) {
            reject(null);
        }
    });

};
const _setPromiseItem = (key, value) => {
    return new Promise((resolve, reject) => {
        try {
            _setItem(key, value);
            resolve(true);
        } catch (e) {
            reject(false);
        }
    });
};



export default LocalStorageService;
