import React from 'react';
import { connect } from 'react-redux';
import { withStyles } from '@material-ui/core/styles';
import PropTypes from 'prop-types';
import FiberManualRecord from '@material-ui/icons/FiberManualRecord';
import styles from './system-checks.style';

const SystemChecks = (props) => {
    const { classes, scenarioSystemCheckList } = props;
    return (
        <div className={classes.constainer + " padding-10"}>
            {scenarioSystemCheckList.map((item, index) => {
                return (
                <div className="mb-10 mt-5" key={index}>
                <p className="fs-12 color-grey">{item['key']}</p>
                <p className="fs-16 color-black">{item['description']}
                    <FiberManualRecord className={classes.bullet + (item['markedChoiceColor'] === 'green' ? ' green' : ' red')}/>
                </p>
                </div>
                );
            })}
        </div>
    );
};

SystemChecks.propTypes = {
    classes: PropTypes.object.isRequired,
  };

const mapStateToProps = state => {
    return {
        markedChoiceColor: state.scenario.markedChoiceColor,
        scenarioSystemCheckList: state.scenario.scenarioSystemCheckList
    };
};

export default connect(mapStateToProps, null)(withStyles(styles)(SystemChecks));
