import CustomHttpService from './custom-http.service';
import Config from '../config/config';
import CookieManagerService from './cookie.service';
import { AppConstant } from '../constants/app.constant';
import EncodeDecodeType from '../actiontype/encode-decode-type';

class SRSummaryService {
    static fetchSRDSummaryTabApi() {
        const config = {
            headers: {
                'Content-Type': "application/x-www-form-urlencoded",
            }
        };
        const lsiNumber = CookieManagerService.getItem(AppConstant.LSI_NUMBER);
        const transactionId = CookieManagerService.getItem(AppConstant.TRANSACTION_ID);
        var url = Config.contextPath + "user/rest/v1.0/srHistory/fetchSRSummary?transactionId="+transactionId +"&lsiNumber=" + lsiNumber;
        return CustomHttpService.get(url, config, EncodeDecodeType.DEFAULT);
    }
}

export default SRSummaryService;
