import React, { Component } from 'react';
import { withStyles } from '@material-ui/core/styles';

const styles = theme => ({
    msgContainer: {
        padding: 40,
        textAlign: 'center'
    }
});

export class LegacyAlert extends Component {
  render() {
      const { classes } = this.props;
    return (
    <div className={classes.msgContainer + " bg-grey ml-20 mt-20 mr-20"}>
        <p>Customer is not registered in Siebel system.</p>
        <p className="mt-10">Please refer to legacy system.</p>
    </div>
    )
  }
}

export default withStyles(styles, { withTheme: true })(LegacyAlert);
