import { SmsDialogActionType } from '../actiontype/sms-dialog.actiontype';
import CommonActionType from '../actiontype/common.actiontype';
const initialState = {
    searchLocationApiresponse: {},
    submitSmsDialogApiresponse: {},
    selectedSMSCategory: '',
    selectedLocations: [],
    selectedSelfCareMsgs: [],
    updatedLocationSearchQuery: '',
    selfCareMessagesApiresponse: [],
    submitSmsSelfCareMsgsApiresponse: {},
};

export default function smsDialogReducer(state = initialState, action) {
    switch (action.type) {
        case SmsDialogActionType.SEARCH_LOCATION_REQUEST:
            return {
                ...state,
                searchLocationApiresponse: action.payload
            };
        case SmsDialogActionType.SEARCH_LOCATION_SUCCESS:
            return {
                ...state,
                searchLocationApiresponse: action.payload
            };
        case SmsDialogActionType.SEARCH_LOCATION_FAILURE:
            return {
                ...state,
                searchLocationApiresponse: action.payload
            };
        case SmsDialogActionType.SUBMIT_SMS_DIALOG_REQUEST:
            return {
                ...state, submitSmsDialogApiresponse: action.payload
            };
        case SmsDialogActionType.SUBMIT_SMS_DIALOG_SUCCESS:
            return {
                ...state,
                submitSmsDialogApiresponse: action.payload
            };
        case SmsDialogActionType.SUBMIT_SMS_DIALOG_FAILURE:
            return {
                ...state,
                submitSmsDialogApiresponse: action.payload
            };
        case SmsDialogActionType.SELECT_SMS_CATEGORY:
            return {
                ...state,
                selectedSMSCategory: action.payload
            }
        case SmsDialogActionType.UPDATE_LOCATION_SEARCH_QUERY:
            return {
                ...state,
                updatedLocationSearchQuery: action.payload
            }
        case SmsDialogActionType.ADD_LOCATION:
            return {
                ...state,
                selectedLocations: [...state.selectedLocations, action.payload]
            }
        case SmsDialogActionType.RESET_SELECTED_LOCATIONS:
            return {
                ...state,
                selectedLocations: [...initialState.selectedLocations]
            }
        case SmsDialogActionType.REMOVE_LOCATION:
            const index = state.selectedLocations.indexOf(action.payload);
            if (index > -1) {
                state.selectedLocations.splice(index, 1);
                return {
                    ...state,
                    selectedLocations: [...state.selectedLocations]
                }
            }
            return {
                ...state
            }
        case SmsDialogActionType.RESET_SMS_DIALOG:
        case CommonActionType.RESET_STATE:
            return {
                ...state,
                ...initialState
            }

        case SmsDialogActionType.SMS_SELF_CARE_MSGS_REQUEST:
            return {
                ...state,
                selfCareMessagesApiresponse: action.payload
            };
        case SmsDialogActionType.SMS_SELF_CARE_MSGS_SUCCESS:
            return {
                ...state,
                selfCareMessagesApiresponse: action.payload
            };
        case SmsDialogActionType.SMS_SELF_CARE_MSGS_FAILURE:
            return {
                ...state,
                selfCareMessagesApiresponse: action.payload
            };

        case SmsDialogActionType.SUBMIT_SMS_SELFCARE_MSGS_REQUEST:
            return {
                ...state, submitSmsSelfCareMsgsApiresponse: action.payload
            };
        case SmsDialogActionType.SUBMIT_SMS_SELFCARE_MSGS_SUCCESS:
            return {
                ...state,
                submitSmsSelfCareMsgsApiresponse: action.payload
            };
        case SmsDialogActionType.SUBMIT_SMS_SELFCARE_MSGS_FAILURE:
            return {
                ...state,
                submitSmsSelfCareMsgsApiresponse: action.payload
            };
        case SmsDialogActionType.ADD_SELF_CARE_MSG:
            return {
                ...state,
                selectedSelfCareMsgs: [...state.selectedSelfCareMsgs, action.payload]
            }
        case SmsDialogActionType.REMOVE_SELF_CARE_MSG:
            const selfCareIndex = state.selectedSelfCareMsgs.indexOf(action.payload);
            if (selfCareIndex > -1) {
                state.selectedSelfCareMsgs.splice(selfCareIndex, 1);
                return {
                    ...state,
                    selectedSelfCareMsgs: [...state.selectedSelfCareMsgs]
                }
            }
            return {
                ...state
            }
        default:
            return state
    }
}
