import { SRSummaryTabActionType } from "../actiontype/sr-summary-tab.actiontype";
import  CommonActionType  from "../actiontype/common.actiontype";


const initialState = {
    resolutionPieChartData:null,
    ffcPieChartData: null,
    subTypePieChartData: null,
    attributedToPieChartData: null
};

export default (state = initialState, action) => {
    switch (action.type) {

        case SRSummaryTabActionType.SET_PIE_CHART_DATA:
            let resolution = action.payload && action.payload.resolution;
            let ffc = action.payload && action.payload.ffc;
            let subType = action.payload && action.payload.subType;
            let attributedTo = action.payload && action.payload.attributedTo;
            let remarks = action.payload && action.payload.remarks;
            return {
                ...state, 
                resolutionPieChartData: resolution,
                ffcPieChartData: ffc,
                subTypePieChartData: subType,
                attributedToPieChartData: attributedTo,
                remarksToPieChartData: remarks

            };
        case CommonActionType.RESET_STATE:
            return {
              ...state,
              ...initialState
            };
        default: 
            return {
                ...state
            }

    }
}
