import { TabsActionType } from "../actiontype/tabs.actiontype";

export function switchHomeTab(payload) {
  return { type: TabsActionType.SWITCH_HOME_TAB, payload };
}

export function setHomeTab(payload) {
  return { type: TabsActionType.SET_HOME_TAB, payload };
}

export function addHomeTab(payload) {
  return { type: TabsActionType.ADD_HOME_TAB, payload };
}

export function switchScenarioTab(payload) {
  return { type: TabsActionType.SWITCH_SCENARIO_TAB, payload };
}

export function setScenarioTab(payload) {
  return { type: TabsActionType.SET_SCENARIO_TAB, payload };
}

export function dispayTopScenarioTab(payload) {
  return { type: TabsActionType.DISPALY_TOP_SCENARIO_TAB, payload };
}

export function hideTopScenarioTab(payload) {
  return { type: TabsActionType.HIDE_TOP_SCENARIO_TAB, payload };
}

export function dispayBillCompareTab(payload) {
  return { type: TabsActionType.DISPALY_BILL_COMPARE_TAB, payload };
}

export function hideBillCompareTab(payload) {
  return { type: TabsActionType.HIDE_BILL_COMPARE_TAB, payload };
}

export function dispayUsageTab(payload) {
  return { type: TabsActionType.DISPALY_USAGE_TAB, payload };
}

export function hideUsageTab(payload) {
  return { type: TabsActionType.HIDE_USAGE_TAB, payload };
}

export function dispaySRDetailsTab(payload) {
  return { type: TabsActionType.DISPALY_SR_DETAILS_TAB, payload };
}

export function hideSRDetailsTab(payload) {
  return { type: TabsActionType.HIDE_SR_DETAILS_TAB, payload };
}

export function dispayRecommendationTab(payload) {
  return { type: TabsActionType.DISPALY_RECOMMENDATION_TAB, payload };
}

export function hideRecommendationTab(payload) {
  return { type: TabsActionType.HIDE_RECOMMENDATION_TAB, payload };
}

export function dispayScenarioTab(payload) {
  return { type: TabsActionType.DISPALY_SCENARIO_TAB, payload };
}

export function displaySRSummaryTab(payload) {
  return { type: TabsActionType.DISPLAY_SR_SUMMARY_TAB, payload };
}

export function displayUtilizationSummaryTab(payload) {
  return { type: TabsActionType.DISPLAY_UTILIZATION_SUMMARY_TAB, payload };
}

export function hideScenarioTab(payload) {
  return { type: TabsActionType.HIDE_SCENARIO_TAB, payload };
}

export function dispayAlertTab(payload) {
  return { type: TabsActionType.DISPALY_ALERT_TAB, payload };
}

export function hideAlertTab(payload) {
  return { type: TabsActionType.HIDE_ALERT_TAB, payload };
}

export const tabManager = (key) => dispatch => {
  switch (key) {
    case TabsActionType.DISPALY_USAGE_TAB:
      return dispatch(dispayUsageTab());

    case TabsActionType.DISPALY_BILL_COMPARE_TAB:
      return dispatch(dispayBillCompareTab());

    case TabsActionType.DISPALY_SR_DETAILS_TAB:
      return dispatch(dispaySRDetailsTab());

    case TabsActionType.DISPALY_RECOMMENDATION_TAB:
      return dispatch(dispayRecommendationTab());

    case TabsActionType.DISPALY_SCENARIO_TAB:
      return dispatch(dispayScenarioTab());

    case TabsActionType.DISPALY_TOP_SCENARIO_TAB:
      return dispatch(dispayTopScenarioTab());

    case TabsActionType.DISPALY_ALERT_TAB:
      return dispatch(dispayAlertTab());

    case TabsActionType.HIDE_USAGE_TAB:
      return dispatch(hideUsageTab());

    case TabsActionType.HIDE_BILL_COMPARE_TAB:
      return dispatch(hideBillCompareTab());

    case TabsActionType.HIDE_SR_DETAILS_TAB:
      return dispatch(hideSRDetailsTab());

    case TabsActionType.HIDE_RECOMMENDATION_TAB:
      return dispatch(hideRecommendationTab());

    case TabsActionType.HIDE_SCENARIO_TAB:
      return dispatch(hideScenarioTab());

    case TabsActionType.HIDE_TOP_SCENARIO_TAB:
      return dispatch(hideTopScenarioTab());

    case TabsActionType.HIDE_ALERT_TAB:
      return dispatch(hideAlertTab());

    case TabsActionType.DISPLAY_SR_SUMMARY_TAB:
      return dispatch(displaySRSummaryTab());

    case TabsActionType.DISPLAY_UTILIZATION_SUMMARY_TAB:
      return dispatch(displayUtilizationSummaryTab());
    default:
      return ''
  }
}

export function switchSRNotesTab(payload) {
  return { type: TabsActionType.SWITCH_SR_NOTES_TAB, payload };
}

export function dispayCreateSRNoteTab(payload) {
  return { type: TabsActionType.DISPLAY_CREATE_SR_NOTE_TAB, payload };
}

export function hideCreateSRNoteTab(payload) {
  return { type: TabsActionType.HIDE_CREATE_SR_NOTE_TAB, payload };
}
