import { createStore, combineReducers, applyMiddleware, compose } from "redux";
import thunk from 'redux-thunk';
import leftNavReducer from "../reducers/left-nav.reducer";
import loginReducer from '../reducers/login.reducer';
import appReducer from "../reducers/app.reducer";
import snackbarReducer from "../reducers/snackbar.reducer";
import dialogReducer from '../reducers/dialog.reducer';
import blankCallReducer from '../reducers/blank-call.reducer';
import scenarioReducer from '../reducers/scenario.reducer';
import SRDetailsTabReducer from "../reducers/sr-details-tab.reducer";
import feedbackDialogReducer from '../reducers/feedback-dialog.reducer';
import callDropDialogReducer from '../reducers/call-drop-dialog.reducer';
import tabReducer from '../reducers/tabs.reducer';
import customerInfoReducer from '../reducers/customer-info.reducer';
import ftrReducer from '../reducers/ftr.reducer';
import callTransferDialogReducer from '../reducers/call-transfer-dialog.reducer';
import smsDialogReducer from '../reducers/sms-dialog.reducer';
import AuthorizationReducer from "../reducers/authorization.reducer";
import appDialogReducer from "../reducers/app-dialog.reducer";
import dffReducer from "../reducers/dff.reducer";
import srReducer from '../reducers/sr.reducer';
import disabledTaggingReducer from '../reducers/disabled-tagging.reducer';
import arcIssueDialogReducer from '../reducers/arc-issue-dialog.reducer';
import predictionReducer from '../reducers/prediction.reducer';
import leapReducer from '../reducers/leap.reducer';
import UsageDetailsTabReducer from '../reducers/usage-details-tab.reducer';
import GLOBAL_CONFIG from '../config/config';
import MultipleTaggingReducer from '../reducers/multiple-tagging.reducer';
import AlertMessageDialogReducer from '../reducers/alert-message-dialog.reducer';
import HandsetDetailsCardReducer from '../reducers/handset-details-card.reducer';
import PaymentDetailsCardReducer from '../reducers/payment-details-card.reducer';
import SystemStatusCardReducer from '../reducers/b2b-system-status-card.reducer';
import SRSummaryTabReducer from '../reducers/b2b-sr-summary-tab.reducer';
import FeedbackDialogService from "../services/feedback-dialog.service";
import FeedbackDialogReducer from "../reducers/feedback-dialog.reducer";
import UtilizationTabReducer from '../reducers/utilization-tab.reducer';
import LsiPingStatusApiReducer from '../reducers/lsi-ping-status-tab.reducer';
import taskUpdateReducer from "../reducers/task-update.reducer";
import UWQReducer from "../reducers/universal-work-queue.reducer";

const middleware = [thunk];
const rootReducer = combineReducers({
    //user auth reducer for /login redirect
    userAuthorized: AuthorizationReducer,
    //
    leftNav: leftNavReducer,
    //this is for loginInfo Object - contain - (LoginApiResponse and LoginStatus)
    loginInfo: loginReducer,
    app: appReducer,
    snackbar: snackbarReducer,
    //this is for SR Details Tab Get Response - contain - (SRDetailsTabStatus and srDetailsTabApiResponse)
    SRDetailsTabApiInfo: SRDetailsTabReducer,
    //this is for all dialog in sidebar
    dialogInfo: dialogReducer,
    //for Blank Call sidebar Api response Object - blankCallApiResponse
    blankCallInfo: blankCallReducer,
    scenario: scenarioReducer,
    //for sidebar feedback call api response - (feedbackCallApiResponse)
    feedbackCallInfo: feedbackDialogReducer,
    //for sidebar calldrop call api response - (callDropApiResponse)
    callDropInfo: callDropDialogReducer,
    tab: tabReducer,
    customerInfo: customerInfoReducer,
    ftr: ftrReducer,
    //for call transfer info
    callTransferInfo: callTransferDialogReducer,
    smsDialogInfo: smsDialogReducer,
    appDialog: appDialogReducer,
    dff: dffReducer,
    sr: srReducer,
    //for all tagging info to avoid duplicate tagging
    disabledTaggingInfo: disabledTaggingReducer,
    prediction: predictionReducer,
    leap: leapReducer,
    //for arc issue dialog sidebar
    arcIssueInfo: arcIssueDialogReducer,
    //for plan and usage details tab
    UsageDetailsTabApiInfo: UsageDetailsTabReducer,
    //multiple tagging for cards
    multipleTaggingInfo: MultipleTaggingReducer,
    //common message dialog
    alertMessageDialog: AlertMessageDialogReducer,
    //handset details card
    HandsetDetailsCardData: HandsetDetailsCardReducer,
    //payment details card
    PaymentDetailsCardData: PaymentDetailsCardReducer,
    // system status card
    SystemStatusCardData: SystemStatusCardReducer,
    // SR Summary Tab
    srSummaryTabData: SRSummaryTabReducer,
    // B2B Feedback Dialog
    b2bFeedbackData: FeedbackDialogReducer,
    // Utilization Tab
    UtilizationTabData: UtilizationTabReducer,
    // Lsi Ping Status Tab
    LsiPingStatusTabData: LsiPingStatusApiReducer,
    srTaskFields: taskUpdateReducer,
    uwq: UWQReducer
})

const createDevStore = () => {
    return createStore(
        rootReducer,
        compose(
            applyMiddleware(...middleware),
            // window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()
        )
    );
}

const createStagingStore = () => {
    return createProdStore();
}

const createProdStore = () => {
    return createStore(
        rootReducer,
        compose(
            applyMiddleware(...middleware)
            //  window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()
        )
    );
}

const store = GLOBAL_CONFIG.env === 'prod' ? createProdStore() : GLOBAL_CONFIG.env === 'staging' ? createStagingStore() : createDevStore();



export default store;
