import React, { Component } from 'react';
import { connect } from 'react-redux';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import Button from '@material-ui/core/Button';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import './login.style.css'; import Select from '@material-ui/core/Select';
import { fetchLoginData } from '../../actions/login.action';
import { withRouter } from 'react-router'
import { LocalStorageService } from '../../services/localstorage.service';
import { HashRouter as Router, Route, Redirect } from 'react-router-dom';
import { openSnackbarRequest } from '../../actions/snackbar.action';
import { withStyles } from '@material-ui/core/styles';
import styles from './login.style';
import newId from '../../services/util.service';

class LoginForm extends Component {
    constructor(props) {
        super(props);
        this.state = { fname: '', lname: '', emailid: '', gender: '', selectedDepartment: '', id: '' };
        this.onLoginHandler = this.onLoginHandler.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }
    componentWillMount() {
        this.id = newId();
    }

    handleChangeDepartmentType = event => {
        this.setState({ selectedDepartment: event.target.value });
    };

    handleChange = prop => event => {
        this.setState({ [prop]: event.target.value });
    };

    resetData = () => {
        this.setState({
            id: '',
            fname: '',
            lname: '',
            emailid: '',
            gender: '',
            department: '',
        })
    };


    onLoginHandler = (event) => {
        event.preventDefault();
        debugger;
        const myObj = {
            id: this.id,
            fname: this.state.fname,
            lname: this.state.lname,
            emailid: this.state.emailid,
            gender: this.state.gender,
            department: this.state.selectedDepartment
        }
        console.log("myObj", myObj);
        LocalStorageService.setRegistationInfo(myObj);
        this.resetData();
        // const { username, password } = this.state;
        // let loginObj = {
        //     username,
        //     password
        // }

        // if (username && password) {
        //     this.props.fetchLoginData(loginObj);
        // }
    }
    render() {

        const { classes } = this.props;
        const isEnabled = this.state.fname;

        const departmentList = ['Engineering', 'Marketing', 'Finance', 'HR']

        return (
            <div>

                <form onSubmit={this.onLoginHandler}>
                    <FormControl fullWidth>
                        <TextField
                            label="fname"
                            placeholder="Enter first name"
                            margin="normal"
                            type="text"
                            value={this.state.fname}
                            onChange={this.handleChange('fname')}
                        />
                    </FormControl>
                    <FormControl fullWidth>
                        <TextField
                            label="lname"
                            placeholder="Enter Last Name"
                            margin="normal"
                            type="text"
                            value={this.state.lname}
                            onChange={this.handleChange('lname')}
                        />
                    </FormControl>
                    <FormControl fullWidth>
                        <TextField
                            label="emailid"
                            placeholder="Enter EmailId"
                            margin="normal"
                            type="text"
                            value={this.state.emailid}
                            onChange={this.handleChange('emailid')}
                        />
                    </FormControl>
                    <FormControl fullWidth>
                        <TextField
                            label="gender"
                            placeholder="Enter gender"
                            margin="normal"
                            type="text"
                            value={this.state.gender}
                            onChange={this.handleChange('gender')}
                        />
                    </FormControl>
                    <FormControl fullWidth className={classes.formControl}>
                        <InputLabel htmlFor="channel-simple">Department</InputLabel>
                        <Select
                            value={this.state.selectedDepartment}
                            onChange={this.handleChangeDepartmentType}
                            input={<Input name="selectedDepartment" id="channel-simple" />}
                        >

                            <MenuItem value="">Department Type</MenuItem>
                            {
                                departmentList.map(type => {
                                    return (
                                        <MenuItem key={type} value={type}>{type}</MenuItem>
                                    )
                                })
                            }
                        </Select>
                    </FormControl>


                    <FormControl className="pull-right loginButton">
                        <Button type="submit" value="Submit" disabled={!isEnabled} variant="contained" size="large" color="primary" onClick={this.onLoginHandler}>LOGIN</Button>
                    </FormControl>
                </form>
            </div>
        )
    }
}
const mapDispatchToProps = dispatch => ({
    fetchLoginData: data => dispatch(fetchLoginData(data)),
    openSnackbarRequest: data => dispatch(openSnackbarRequest(data)),
});
const mapStateToProps = state => {
    return {
        loginStatus: state.loginInfo.loginStatus,
        loginApiResponse: state.loginInfo.loginApiResponse
    };
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(withStyles(styles)(LoginForm)));
