const styles = theme => ({
    root: {
        flexGrow: 1,
        padding: '10px 20px'
    },
    tabsRoot: {
        borderBottom: '1px solid #e8e8e8',
    },
    tabsIndicator: {
        backgroundColor: '#03a9f4',
    },
    tabRoot: {
        textTransform: 'initial',
        minWidth: 72,
        fontSize: 16,
        fontWeight: theme.typography.fontWeightRegular,
        marginRight: theme.spacing.unit * 4,
        fontFamily: [
            '-apple-system',
            'BlinkMacSystemFont',
            '"Segoe UI"',
            'Roboto',
            '"Helvetica Neue"',
            'Arial',
            'sans-serif',
            '"Apple Color Emoji"',
            '"Segoe UI Emoji"',
            '"Segoe UI Symbol"',
        ].join(','),
        '&:hover': {
            color: '#03a9f4',
            opacity: 1,
        },
        '&$tabSelected': {
            color: '#03a9f4',
            fontWeight: theme.typography.fontWeightMedium,
        },
        '&:focus': {
            color: '#03a9f4',
        },
    },
    tabSelected: {},
    typography: {
        padding: theme.spacing.unit * 3,
    },
});
export default styles;
