import React, { Component } from 'react'
import Tooltips from '../tooltip/tooltip.dumb';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';

const SrHistoryRowDetail = (props) => {
    const tooltipPosition = "bottom-start";
    let { data, closingExpansionPanel, openSRNotesDialog, reOpenSr, srDetailsProcessedData, openSrTaskUpdateCaseSelectorDialog} = props;

    function onClickViewNotes(e, data) {
        e.stopPropagation();
        openSRNotesDialog(data);
    }

    function onClickReOpenSr(e, srNumber) {
        e.stopPropagation();
        reOpenSr(srNumber);
    }

    function onClickAssignTaskButton(e, srNumber) {
        e.stopPropagation()
        openSrTaskUpdateCaseSelectorDialog(srNumber)
    }

    function showReOpenSrButton(srData, typeSubtypeOpenSrExists) {
        let result = false
        if(srData) {
            let isResolved = srData.problem_activityStatus && srData.problem_activityStatus.toLowerCase() === 'resolved'
            let isTatExceeded = false
            if(isResolved && srData.problem_resolutionTime) {
                const resolutionDateTime = new Date(Date.parse(srData.problem_resolutionTime))
                const currentDateTime = new Date()
                var diff = Math.abs(currentDateTime.getTime() - resolutionDateTime.getTime()) / 3600000;
                if(diff > 24) {
                    isTatExceeded = true
                }
            }
            let key = `${srData['problem_type']}-${srData['problem_subType']}-${srData['problem_subSubType']}`
            result = (isResolved && !isTatExceeded && typeSubtypeOpenSrExists && !typeSubtypeOpenSrExists[key])
        }
        return result
    }

    function showAssignTaskButton(srData) {
        let result = false
        if(srData) {
            result = srData.problem_activityStatus && (srData.problem_activityStatus.toLowerCase() === 'open' || srData.problem_activityStatus.toLowerCase() === 're-open')
        }
        return result
    }

    return (
        <div className="pt-20 pb-20 pl-5 pr-5 bg-grey clickable" onClick={() => closingExpansionPanel()}>
            <div style={{ display: 'flex', flexwrap: 'wrap' }}>
                <div className="mr-20 mb-20 width-16">
                    <p className="fs-12 color-707070 mb-5">SR NUMBER</p>
                    <Tooltips tooltipText={data.problem_problemId} position={tooltipPosition}>
                        <Typography className="field-value" variant="inherit" noWrap>
                            {data.problem_problemId}
                        </Typography>
                    </Tooltips>
                </div>
                {data &&
                    <div className="mr-20 mb-20 width-16">
                        <p className="fs-12 color-707070 mb-5">TYPE</p>
                        <Tooltips tooltipText={data.problem_type} position={tooltipPosition}>
                            <Typography className="field-value" variant="inherit" noWrap>
                                {data.problem_type}
                            </Typography>
                        </Tooltips>
                    </div>
                }
                {data &&
                    <div className="mr-20 mb-20 width-16">
                        <p className="fs-12 color-707070 mb-5">SUB TYPE</p>
                        <Tooltips tooltipText={data.problem_subType} position={tooltipPosition}>
                            <Typography className="field-value" variant="inherit" noWrap>
                                {data.problem_subType}
                            </Typography>
                        </Tooltips>
                    </div>
                }
                {
                    data &&
                    <div className="mr-20 mb-20 width-16">
                        <p className="fs-12 color-707070 mb-5">SUB SUB TYPE</p>
                        <Tooltips tooltipText={data.problem_subSubType} position={tooltipPosition}>
                            <Typography className="field-value" variant="inherit" noWrap>
                                {data.problem_subSubType}
                            </Typography>
                        </Tooltips>
                    </div>
                }
                {
                    data &&
                    <div className="mr-20 mb-20 width-16">
                        <p className="fs-12 color-707070 mb-5">STATUS</p>
                        <Tooltips tooltipText={data.problem_activityStatus} position={tooltipPosition}>
                            <Typography className="field-value" variant="inherit" noWrap>
                                {data.problem_activityStatus}
                            </Typography>
                        </Tooltips>
                    </div>
                }
                {
                    data &&
                    <div className="mr-20 mb-20 width-16">
                        <p className="fs-12 color-707070 mb-5">CREATE TIME</p>
                        <Tooltips tooltipText={data.problem_createdDate} position={tooltipPosition}>
                            <Typography className="field-value" variant="inherit" noWrap>
