import React, { Component } from 'react';
import { connect } from 'react-redux';
import DialogActions from '@material-ui/core/DialogActions';
import { makeRequestForAutoStep } from '../../../../actions/scenario.action';
import DialogContent from '@material-ui/core/DialogContent';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import '../../../../index.css';
import CustomButton from '../../custom-button/custom-button.dumb';
import styles from '../dialog-style'

class ProvisioningConfirmationDialog extends Component {

    constructor(props) {
        super(props);
        this.confirmNoAction = this.confirmNoAction.bind(this);
        this.confirmYesAction = this.confirmYesAction.bind(this);
        // this.handleCloseModal = this.handleCloseModal.bind(this);
    }



    confirmYesAction = () => {
        this.props.makeRequestForScenarioAutoStep(this.props.nextStepData, 'true');
        this.props.closeDialogRequest();
    }

    confirmNoAction = () => {
        this.props.makeRequestForScenarioAutoStep(this.props.nextStepData, 'false');
        this.props.closeDialogRequest();
    }

    render() {
        const { message } = this.props;
        return (
            <React.Fragment>
                <DialogContent>
                    {message}
                </DialogContent>
                {/* <DialogActions className="ml-25 mr-25 mt-25 mb-25"> */}
                <DialogActions>
                    <CustomButton title="NO" onClick={this.confirmNoAction} color="red" />
                    <CustomButton title="YES" onClick={this.confirmYesAction} color="green" />
                    {/* </DialogActions> */}
                </DialogActions>
            </React.Fragment >

        )
    }
}
const mapDispatchToProps = dispatch => ({
    makeRequestForScenarioAutoStep: (nextStepData, provConfirm) => dispatch(makeRequestForAutoStep(nextStepData, provConfirm))
});
const mapStateToProps = state => {
    return {
        message: state.scenario.provConfirmInfo && state.scenario.provConfirmInfo.provConfirmDialogMessage,
        nextStepData: state.scenario.provConfirmInfo.nextStepData
    };
}

ProvisioningConfirmationDialog.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default connect(
    mapStateToProps, mapDispatchToProps
)(withStyles(styles)(ProvisioningConfirmationDialog));
