import { CallDropDialogActionType } from '../actiontype/call-drop-dialog.actiontype';
const initialState = {
  //submitting call drop details
  submitCallDropApiResponse: {},
  //fetching call drop details
  fetchCallDropApiResponse: {},
  fetchSetDropCallScenarioName: '',
  fetchSetDropCallScenarioId: '',
  fetchDropCallsystemInfoObj: '',
};

export default function callDropDialogReducer(state = initialState, action) {
  switch (action.type) {
    case CallDropDialogActionType.SUBMIT_CALL_DROP_DIALOG_REQUEST:
      return {
        ...state, submitCallDropApiResponse: action.payload
      };
    case CallDropDialogActionType.SUBMIT_CALL_DROP_DIALOG_SUCCESS:
      return {
        ...state,
        submitCallDropApiResponse: action.payload
      };
    case CallDropDialogActionType.SET_DROPCALL_SYSTEM_INFO:
      return {
        ...state,
        fetchDropCallsystemInfoObj: action.payload
      };
    case CallDropDialogActionType.SUBMIT_CALL_DROP_DIALOG_FAILURE:
      return {
        ...state,
        submitCallDropApiResponse: action.payload
      };
    case CallDropDialogActionType.FETCH_CALL_DROP_DIALOG_REQUEST:
      return {
        ...state, fetchCallDropApiResponse: action.payload
      };
    case CallDropDialogActionType.FETCH_CALL_DROP_DIALOG_SUCCESS:
      return {
        ...state,
        fetchCallDropApiResponse: action.payload
      };
    case CallDropDialogActionType.FETCH_CALL_DROP_DIALOG_FAILURE:
      return {
        ...state,
        fetchCallDropApiResponse: action.payload
      };
    case CallDropDialogActionType.SET_DROP_CALL_SCENARIONAME_SUCCESS:
      return {
        ...state,
        fetchSetDropCallScenarioName: action.payload
      };
    case CallDropDialogActionType.SET_DROP_CALL_SCENARIOID_SUCCESS:
      return {
        ...state,
        fetchSetDropCallScenarioId: action.payload
      };

    default:
      return state
  }
}
