import { PaymentDetailsActionType } from '../actiontype/payment-details-card.actiontype';
import CommonActionType from '../actiontype/common.actiontype';
import { CustomerInfoActionType } from '../actiontype/customer-info.actiontype';
const initialState = {
    PaymentDetailsCardStatus: '',
    PaymentDetailsCardDataResponse: {},
    paymentScenarioCardLinksData: {},
    selectedPaymentHistoryDetail: [],
    sendSmsPaymentDetailDataSuccess: []
};

export default function PaymentDetailsCardReducer(state = initialState, action) {
    switch (action.type) {
        case PaymentDetailsActionType.PAYMENT_DETAILS_CARD_REQUEST:
            return {
                ...state,
                PaymentDetailsCardStatus: "loading",
                PaymentDetailsCardDataResponse: action.payload
            };
        case PaymentDetailsActionType.PAYMENT_DETAILS_CARD_SUCCESS:
            return {
                ...state,
                PaymentDetailsCardStatus: true,
                PaymentDetailsCardDataResponse: action.payload
            };
        case PaymentDetailsActionType.PAYMENT_DETAILS_CARD_FAILURE:
            return {
                ...state,
                PaymentDetailsCardStatus: false,
            };
        case PaymentDetailsActionType.SET_PAYMENT_SCENARIO_CARD_LINKS:
            return {
                ...state,
                paymentScenarioCardLinksData: action.payload
            };


        case PaymentDetailsActionType.ADD_PAYMENT_HISTORY_OBJ:
            return {
                ...state,
                selectedPaymentHistoryDetail: [...state.selectedPaymentHistoryDetail, action.payload]
            }
        case PaymentDetailsActionType.REMOVE_PAYMENT_HISTORY_OBJ:
            const selectedPaymentObjIndex = state.selectedPaymentHistoryDetail.indexOf(action.payload);
            if (selectedPaymentObjIndex > -1) {
                state.selectedPaymentHistoryDetail.splice(selectedPaymentObjIndex, 1);
                return {
                    ...state,
                    selectedPaymentHistoryDetail: [...state.selectedPaymentHistoryDetail]
                }
            }
            return {
                ...state
            }
        case PaymentDetailsActionType.SUBMIT_PAYMENT_EMAIL_REQUEST:
            return {
                ...state,
                PaymentDetailsCardStatus: "loading",
                sendSmsPaymentDetailDataSuccess: action.payload
            };
        case PaymentDetailsActionType.SUBMIT_PAYMENT_EMAIL_SUCCESS:
            return {
                ...state,
                PaymentDetailsCardStatus: true,
                sendSmsPaymentDetailDataSuccess: action.payload
            };
        case PaymentDetailsActionType.SUBMIT_SMS_PAYMENT_EMAIL_FAILURE:
            return {
                ...state,
                PaymentDetailsCardStatus: false,
            };
        case CustomerInfoActionType.RESET_CUSTOMER:
        case CommonActionType.RESET_STATE:
            return {
                ...state,
                ...initialState
            }
        default:
            return state
    }

}
