import React, { Component } from 'react'
import { connect } from 'react-redux';
import Grid from '@material-ui/core/Grid';
import '../../../index.css';
import { fetchSRDetailsTabData, showSRHistoryTab } from '../../../actions/sr-details-tab.action';
import SystemTypeService from '../../../services/system-type.service';
import { withStyles } from '@material-ui/core/styles';
import { openPreviousInteractionsDialog } from '../../../actions/dialog.action';
import NavigateBefore from '@material-ui/icons/NavigateBefore';
import { Pie } from "react-chartjs-2";
import Typography from '@material-ui/core/Typography';

class SRSummaryTab extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    const { classes, ffcPieChart, resolutionPieChart, remarksToPieChart, attributedToPieChart } = this.props;
    return (
      <div className="mt-25">
        <Grid container spacing={24}>
              {attributedToPieChart && attributedToPieChart.lables &&
              <Grid className="width-33 mt-25">
                  <h3 className="mt-5 text-center mb-25">Attribution Breakdown</h3>
                  <Pie data={attributedToPieChart} options={{ layout:{position: "left"}, maintainAspectRatio: false, legend: {width:"20px" , display: false, position: 'right', size: "10px"}, responsive: false }} />
              </Grid>}
              { ffcPieChart && ffcPieChart.labels && 
              <Grid className="width-33 mt-25">
                  <h3 className="mt-5 text-center mb-25">FFC Breakdown</h3>
                  <Pie data={ffcPieChart} options={{ layout:{position: "left"}, maintainAspectRatio: false, legend: {width:"20px" , display: false, position: 'right', size: "10px"}, responsive: false }} />
              </Grid>}
              { resolutionPieChart && resolutionPieChart.labels &&
              <Grid className="width-33 mt-25">
                  <h3 className="mt-5 text-center mb-25">Resolution Breakdown</h3>
                  <Pie data={resolutionPieChart} options={{ layout:{position: "left"}, maintainAspectRatio: false, legend: {width:"20px" ,display: false, position: 'right', size:"10px"}, responsive: false }} />
              </Grid>}
              { (!attributedToPieChart || !attributedToPieChart.labels) && 
                (!ffcPieChart || !ffcPieChart.labels) && 
                (!resolutionPieChart || !resolutionPieChart.labels) && 
                <Grid className="width-100 padding-40 text-center">
                  <Typography>
                    No valid SR found in last 6 months
                  </Typography>
                </Grid>
              }
       </Grid>

       {remarksToPieChart && remarksToPieChart.maxIssue && 
       <h2 className="mt-40 text-center mb-25">
          Services of this customer are generally impacted due to <span className="text-capitalize">{remarksToPieChart.maxIssue}</span>
       </h2>}
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
     ffcPieChart: state.srSummaryTabData.ffcPieChartData,
    resolutionPieChart: state.srSummaryTabData.resolutionPieChartData,
    subTypePieChart: state.srSummaryTabData.subTypePieChartData,
    attributedToPieChart: state.srSummaryTabData.attributedToPieChartData,
    remarksToPieChart: state.srSummaryTabData.remarksToPieChartData
  };
}

export default connect(
  mapStateToProps
)(withStyles()(SRSummaryTab));
