import React, { Component } from 'react';
import { connect } from 'react-redux';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle'
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import '../../../../index.css';
import styles from '../dialog-style'
import FormGroup from '@material-ui/core/FormGroup';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import { searchLocationDialogData, submitSmsDialogdata, selectSMSCategory, updateLocationSearchQuery, addLocation, removeLocation, closeAndResetSMSDialog, getSelfCareMessages, addSelfCareMsg, removeSelfCareMsg, submitSelfCareMessagesDialogdata } from '../../../../actions/sms-dialog.action';
import SMSCategory from '../../../../constants/sms-category.enum';
import { stat } from 'fs';
import LeftNavMenuKey from '../../../../constants/left-nav-menu.enum';

class SmsDialog extends Component {
    constructor(props) {
        super(props);
        this.handleCloseModal = this.handleCloseModal.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSearchLocation = this.handleSearchLocation.bind(this);
        this.submitSmsData = this.submitSmsData.bind(this);
        this.handleLocationSearchInputChange = this.handleLocationSearchInputChange.bind(this);
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
    }

    handleCloseModal = () => {
        this.props.closeAndResetSMSDialog();
    }

    handleFormSubmit = event => {
        event.preventDefault();
    }

    handleLocationSearchInputChange = event => {
        event.preventDefault();
        this.props.updateLocationSearchQuery(event.target.value);
    }
    handleChange = event => {
        event.preventDefault();
        this.props.selectSMSCategory(event.target.value);
        if (event.target.value === SMSCategory.SELF_CARE) {
            this.props.getSelfCareMessages(this.props.scenarioName);
        }
    };
    handleSearchLocation = (event) => {
        event.preventDefault();
        this.props.searchLocationDialogData();
    };
    submitSmsData = (event) => {
        event.preventDefault();
        this.props.submitSmsDialogdata();
    }
    addLocationAddress = (event, location) => {
        if (event.target.checked) {
            this.props.addLocation(location);
        }
        else {
            this.props.removeLocation(location);
        }
    };
    addSelfCareMessageTemplate = (event, selfcareMsgObj) => {
        if (event.target.checked) {
            this.props.addSelfCareMsg(selfcareMsgObj);
        }
        else {
            this.props.removeSelfCareMsg(selfcareMsgObj);
        }
    };

    isSubmitButtonDisabled = () => {
        const { selectedSMSCategory, selectedLocations, selectedSelfCareMsgs } = this.props;
        if (selectedSMSCategory) {
            if (selectedSMSCategory === SMSCategory.AIRTEL_STORE && selectedLocations.length) {
                return false;
            } else if (selectedSMSCategory === SMSCategory.SELF_CARE && selectedSelfCareMsgs.length) {
                return false;
            }
        }
        return true;
    }

    render() {
        const smsDialogTitle = this.props.smsDialogTitle;
        const smsDialogStatus = this.props.smsDialogStatus;
        const { classes, selfHelpTabDisabled, selectedLocations, searchLocationApiresponse, selectedSMSCategory, updatedLocationSearchQuery, selfCareMessagesList, selectedSelfCareMsgs } = this.props;
        return (
            <React.Fragment>
                <Dialog
                    open={smsDialogStatus}
                    onClose={this.handleCloseModal}
                    maxWidth={`sm`}
                    fullWidth={true}
                    aria-labelledby="form-dialog-title"
                >
                    <DialogTitle id="form-dialog-title">
                        {smsDialogTitle}
                        <IconButton aria-label="Close" className={classes.closeButton} onClick={this.handleCloseModal}>
                            <CloseIcon />
                        </IconButton>
                    </DialogTitle>
                    <DialogContent>
                        <form onSubmit={this.handleFormSubmit} className={classes.root} autoComplete="off">
                            <FormControl fullWidth className={classes.formControl}>
                                <InputLabel htmlFor="sms-simple">Select Option</InputLabel>
                                <Select
                                    value={selectedSMSCategory}
                                    onChange={this.handleChange}
                                    input={<Input name="smsCategory" id="sms-simple" />}
                                    disabled={selfHelpTabDisabled}
                                >
                                    <MenuItem value={SMSCategory.AIRTEL_STORE}>{SMSCategory.AIRTEL_STORE}</MenuItem>
                                    <MenuItem value={SMSCategory.SELF_CARE}>{SMSCategory.SELF_CARE}</MenuItem>
                                </Select>
                            </FormControl>

                            {/* for selected category */}
                            {selectedSMSCategory && selectedSMSCategory === SMSCategory.AIRTEL_STORE &&
                                <div className="position-relative">
                                    <FormControl fullWidth>
                                        <TextField
                                            id="standard-with-placeholder"
                                            label="Search Locality"
                                            placeholder="Search Locality"
                                            margin="normal"
                                            value={updatedLocationSearchQuery}
                                            // onKeyPress={this.handleKeyPressOnLocationSearch}
                                            onChange={this.handleLocationSearchInputChange}
                                        />

                                        <Button disabled={!updatedLocationSearchQuery && !updatedLocationSearchQuery.length} onClick={this.handleSearchLocation} variant="contained" className="sms-search-btn disabledButton">
                                            Search
                                        </Button>
                                    </FormControl>
                                </div>
                            }

                            {/* for selecting location  */}
                            <div>
                                {selectedSMSCategory && selectedSMSCategory === SMSCategory.AIRTEL_STORE && searchLocationApiresponse && searchLocationApiresponse !== "" && searchLocationApiresponse.length &&
                                    <Paper className={classes.customScrollableBox} elevation={1}>
                                        <FormControl component="fieldset" className={classes.formControl}>
                                            <FormGroup>
                                                {searchLocationApiresponse.length && searchLocationApiresponse.map((item, index) => {
                                                    return (
                                                        <FormControlLabel className='fs-14' key={index} value={item.address} control={<Checkbox classes={{
                                                            root: classes.check,
                                                            checked: classes.checked,
                                                        }} onChange={event => this.addLocationAddress(event, item.address)} />} checked={selectedLocations.indexOf(item.address) > -1} label={item.address} />
                                                    )
                                                })}
                                            </FormGroup>

                                        </FormControl>
                                    </Paper>
                                }
                            </div>
                            <div>
                                {selectedSMSCategory && selectedSMSCategory === SMSCategory.SELF_CARE &&
                                    <Typography component="h3" className={classes.titlebarSms}>Select & Send Message To Customer </Typography>
                                }
                            </div>

                            {/* for selecting self care messages  */}
                            <div>
                                {
                                    selectedSMSCategory && selectedSMSCategory === SMSCategory.SELF_CARE &&

                                    <Paper className={classes.customScrollableBox} elevation={1}>
                                        <FormControl component="fieldset" className={classes.formControl}>
                                            <FormGroup>
                                                {selfCareMessagesList && selfCareMessagesList.length && selfCareMessagesList.map((item, index) => {
                                                    return (
                                                        <FormControlLabel className='fs-14 lableList' key={item.id} value={item.templateName} control={<Checkbox classes={{
                                                            root: classes.check,
                                                            checked: classes.checked,
                                                        }} onChange={event => this.addSelfCareMessageTemplate(event, item.templateName)} checked={selectedSelfCareMsgs.indexOf(item.templateName) > -1} />} label={item.message} />
                                                    )
                                                })}

                                            </FormGroup>

                                        </FormControl>
                                    </Paper>
                                }
                            </div>


                        </form>

                    </DialogContent>
                    <DialogActions className="ml-25 mr-25 mt-25 mb-25">
                        <Button variant="contained" disabled={this.isSubmitButtonDisabled()} size="large" className="bgc-blue disabledButton color-white" onClick={this.submitSmsData}>SUBMIT</Button>
                    </DialogActions>
                </Dialog>
            </React.Fragment >

        )
    }
}
const mapDispatchToProps = dispatch => ({
    closeAndResetSMSDialog: data => dispatch(closeAndResetSMSDialog(data)),
    searchLocationDialogData: data => dispatch(searchLocationDialogData(data)),
    submitSmsDialogdata: data => dispatch(submitSmsDialogdata(data)),
    selectSMSCategory: smsCategory => dispatch(selectSMSCategory(smsCategory)),
    updateLocationSearchQuery: locationSearchQuery => dispatch(updateLocationSearchQuery(locationSearchQuery)),
