class MultipleTaggingService {
    static getMultipleDynamicKey(multipleTaggingCardSource, dynamicKey) {
        if (multipleTaggingCardSource && dynamicKey) {
            return multipleTaggingCardSource + "_" + dynamicKey;
        }
        return null;
    }
    static getCardSource(key, multipleDynamicTaggingKey) {
        if (multipleDynamicTaggingKey) {
            return multipleDynamicTaggingKey;
        }
        return key;
    }
    static getCardLinksCrmCodeType(cardLinks, crmCodeType) {
        if (cardLinks && crmCodeType) {
            let filteredCardLinks = cardLinks.filter(data => data['type'].toLowerCase().trim() === crmCodeType);
            return filteredCardLinks;
        }
        return cardLinks;

    }
    static getCardLinksScenarioType(cardLinks) {

        if (cardLinks) {
            let filteredCardLinks = cardLinks.filter(data => data['type'].toLowerCase().trim() === 'scenario');
            return filteredCardLinks;
        }
        return cardLinks;

    }
}

export default MultipleTaggingService;
