const styles = theme => ({
  root: {
    flexGrow: 1,
    padding: '0px 20px',
    position: 'relative'

  },
  tabsRoot: {
    backgroundColor: '#fafafa',
    zIndex: 2
  },
  tabsIndicator: {
    backgroundColor: '#03a9f4',
  },
  tabRoot: {
    textTransform: 'initial',
    minWidth: 72,
    fontSize: 16,
    fontWeight: theme.typography.fontWeightRegular,
    marginRight: theme.spacing.unit * 4,
    '&$tabSelected': {
      color: '#03a9f4',
      fontWeight: theme.typography.fontWeightMedium,
    },
    '&:focus': {
      color: '#03a9f4',
    },
  },
  tabSelected: {},
  typography: {
    textAlign: 'left'
  },
  scrollable: {
    overflowX: 'hidden'
  },
  bulletCss: {
    position: 'absolute',
    right: 0,
    fontSize: 16,
    '&.green': {
      color: '#759B13'
    },
    '&.red': {
      color: '#d71b28'
    }
  }
});

export default styles;
