import { ArcIssueDialogActionType } from '../actiontype/arc-issue-dialog.actiontype';
const initialState = {
    fetchArcIssueApiResponse: {},
    fetchArcIssueTypeApiResponse: {},
    fetchArcIssueSubTypeApiResponse: [],
    fetchArcIssueDialogObj: {},
    fetchArcIssueVocApiResponse: {},
    fetchArcIssueCityApiResponse: [],
    fetchArcIssueLocationApiResponse: [],
    fetchArcStoreNameAddrApiResponse: [],
    fetchArcIssueChannelApiResponse: [],
    fetchArcIssueCircleApiResponse: [],
    fetchArcStoreTypeApiResponse: [],
    selectedArcIssueChannel: '',
    selectedArcIssueCircle: '',
    selectedArcIssueType: '',
    selectedArcIssueSubType: '',
    selectedArcIssueCity: '',
    selectedArcIssueVoc: '',
    selectedArcIssueLocation: '',
    selectedArcIssueStoreNameAddr: '',
    selectedArcIssueStoreType: '',
    submitArcIssueDialogApiResponse: {},
    submitArcIssueEmailApiResponse: {},
    selectedArcIssueVocObject: {},
    selectedArcIssueStoreObject: {},
    selectedArcNotes: '',
    selectedArcStoreId: '',
};

export default function arcIssueDialogReducer(state = initialState, action) {
    switch (action.type) {
        case ArcIssueDialogActionType.RESET_ARC_ISSUE_DIALOG_DATA:
            return {
                ...state,
                ...initialState
            };
        case ArcIssueDialogActionType.FETCH_ARC_ISSUE_CHANNEL_SUCCESS:
            return {
                ...state,
                fetchArcIssueChannelApiResponse: action.payload
            };
        case ArcIssueDialogActionType.FETCH_ARC_ISSUE_CHANNEL_FAILURE:
            return {
                ...state,
                fetchArcIssueChannelApiResponse: action.payload
            };

        case ArcIssueDialogActionType.FETCH_ARC_ISSUE_CIRCLE_SUCCESS:
            return {
                ...state,
                fetchArcIssueCircleApiResponse: action.payload
            };
        case ArcIssueDialogActionType.FETCH_ARC_ISSUE_CIRCLE_FAILURE:
            return {
                ...state,
                fetchArcIssueCircleApiResponse: action.payload
            };

        case ArcIssueDialogActionType.FETCH_ARC_ISSUE_TYPE_SUCCESS:
            return {
                ...state,
                fetchArcIssueTypeApiResponse: action.payload
            };
        case ArcIssueDialogActionType.FETCH_ARC_ISSUE_TYPE_FAILURE:
            return {
                ...state,
                fetchArcIssueTypeApiResponse: action.payload
            };

        case ArcIssueDialogActionType.FETCH_ARC_ISSUE_DIALOG_DATA_SUCCESS:
            return {
                ...state,
                fetchArcIssueDialogObj: action.payload
            };

        case ArcIssueDialogActionType.FETCH_ARC_ISSUE_SUB_TYPE_SUCCESS:
            return {
                ...state,
                fetchArcIssueSubTypeApiResponse: action.payload
            };
        case ArcIssueDialogActionType.FETCH_ARC_ISSUE_SUB_TYPE_FAILURE:
            return {
                ...state,
                fetchArcIssueSubTypeApiResponse: action.payload
            };

        case ArcIssueDialogActionType.FETCH_ARC_ISSUE_VOC_SUCCESS:
            return {
                ...state,
                fetchArcIssueVocApiResponse: action.payload
            };
        case ArcIssueDialogActionType.FETCH_ARC_ISSUE_VOC_FAILURE:
            return {
                ...state,
                fetchArcIssueVocApiResponse: action.payload
            };

        case ArcIssueDialogActionType.FETCH_ARC_ISSUE_CITY_SUCCESS:
            return {
                ...state,
                fetchArcIssueCityApiResponse: action.payload
            };
        case ArcIssueDialogActionType.FETCH_ARC_ISSUE_CITY_FAILURE:
            return {
                ...state,
                fetchArcIssueCityApiResponse: action.payload
            };

        case ArcIssueDialogActionType.FETCH_ARC_ISSUE_LOCATION_SUCCESS:
            return {
                ...state,
                fetchArcIssueLocationApiResponse: action.payload
            };
        case ArcIssueDialogActionType.FETCH_ARC_ISSUE_LOCATION_FAILURE:
            return {
                ...state,
                fetchArcIssueLocationApiResponse: action.payload
            };

        case ArcIssueDialogActionType.FETCH_ARC_STORE_NAME_ADDR_SUCCESS:
            return {
                ...state,
                fetchArcStoreNameAddrApiResponse: action.payload
            };
        case ArcIssueDialogActionType.FETCH_ARC_STORE_NAME_ADDR_FAILURE:
            return {
                ...state,
                fetchArcStoreNameAddrApiResponse: action.payload
            };

        case ArcIssueDialogActionType.FETCH_ARC_STORE_TYPE_SUCCESS:
            return {
                ...state,
                fetchArcStoreTypeApiResponse: action.payload
            };
        case ArcIssueDialogActionType.FETCH_ARC_STORE_TYPE_FAILURE:
            return {
                ...state,
                fetchArcStoreTypeApiResponse: action.payload
            };

        case ArcIssueDialogActionType.SET_ARC_ISSUE_CHANNEL_SUCCESS:
            return {
                ...state,
                selectedArcIssueChannel: action.payload
            };
        case ArcIssueDialogActionType.SET_ARC_ISSUE_CIRCLE_SUCCESS:
            return {
                ...state,
                selectedArcIssueCircle: action.payload
            };
        case ArcIssueDialogActionType.SET_ARC_ISSUE_TYPE_SUCCESS:
            return {
                ...state,
                selectedArcIssueType: action.payload
            };
        case ArcIssueDialogActionType.SET_ARC_ISSUE_SUB_TYPE_SUCCESS:
            return {
                ...state,
                selectedArcIssueSubType: action.payload
            };
        case ArcIssueDialogActionType.SET_ARC_ISSUE_CITY_SUCCESS:
            return {
                ...state,
                selectedArcIssueCity: action.payload
            };
        case ArcIssueDialogActionType.SET_ARC_ISSUE_LOCATION_SUCCESS:
            return {
                ...state,
                selectedArcIssueLocation: action.payload
            };
        case ArcIssueDialogActionType.SET_ARC_ISSUE_STORE_NAME_SUCCESS:
            return {
                ...state,
                selectedArcIssueStoreNameAddr: action.payload
            };
        case ArcIssueDialogActionType.SET_ARC_ISSUE_STORE_TYPE_SUCCESS:
            return {
                ...state,
                selectedArcIssueStoreType: action.payload
            };
        case ArcIssueDialogActionType.SET_ARC_ISSUE_VOC_SUCCESS:
            return {
                ...state,
                selectedArcIssueVoc: action.payload
            };

        case ArcIssueDialogActionType.SET_ARC_SELECTED_VOC_OBJ_SUCCESS:
            return {
                ...state,
                selectedArcIssueVocObject: action.payload
            };

        case ArcIssueDialogActionType.SET_ARC_SELECTED_STORE_OBJ_SUCCESS:
            return {
                ...state,
                selectedArcIssueStoreObject: action.payload
            };

        case ArcIssueDialogActionType.SUBMIT_ARC_ISSUE_REQUEST:
            return {
                ...state,
                submitArcIssueDialogApiResponse: action.payload
            };
        case ArcIssueDialogActionType.SUBMIT_ARC_ISSUE_SUCCESS:
            return {
                ...state,
                submitArcIssueDialogApiResponse: action.payload
            };
        case ArcIssueDialogActionType.SUBMIT_ARC_ISSUE_FAILURE:
            return {
                ...state,
                submitArcIssueDialogApiResponse: action.payload
            };

        case ArcIssueDialogActionType.SUBMIT_ARC_EMAIL_REQUEST:
            return {
                ...state,
                submitArcIssueEmailApiResponse: action.payload
            };
        case ArcIssueDialogActionType.SUBMIT_ARC_EMAIL_SUCCESS:
            return {
                ...state,
                submitArcIssueEmailApiResponse: action.payload
            };
        case ArcIssueDialogActionType.SUBMIT_ARC_EMAIL_FAILURE:
            return {
                ...state,
                submitArcIssueEmailApiResponse: action.payload
            };


        case ArcIssueDialogActionType.SET_ARC_ISSUE_NOTES_SUCCESS:
            return {
                ...state,
                selectedArcNotes: action.payload
            };
        case ArcIssueDialogActionType.SET_ARC_ISSUE_STORE_ID_SUCCESS:
            return {
                ...state,
                selectedArcStoreId: action.payload
            };

        default:
            return state
    }
}
