import { AppDialogActionType } from "../actiontype/app-dialog.actiontype";

export function openDFFDialogRequest() {
    return { type: AppDialogActionType.OPEN_DFF_REQUEST };
}

export function openClaimDFFDialogRequest() {
    return { type: AppDialogActionType.OPEN_CLAIM_DFF_REQUEST };
}

export function diaplayFTRMessageDialog() {
    return { type: AppDialogActionType.DISPLAY_FTR_MESSAGE };
}

export function diaplaySRMessageRequest() {
    return { type: AppDialogActionType.DISPLAY_SR_MESSAGE };
}

export function closeAppDialogRequest() {
    return { type: AppDialogActionType.CLOSE_APP_DIALOG };
}

export function displayDNDCategoriesRequest() {
    return { type: AppDialogActionType.DISPLAY_DND_CATEGORIES };
}

export function createFTRRequest() {
    return { type: AppDialogActionType.CREATE_FTR };
}
export function diaplayArcSuccessMessageDialog() {
    return { type: AppDialogActionType.DISPLAY_ARC_SUCCESS_MESSAGE };
}

export function openAuthMatrixDialog() {
    return { type: AppDialogActionType.OPEN_AUTH_MATRIX };
}
export function openPaymentHistoryDialog() {
    return { type: AppDialogActionType.OPEN_PAYMENT_HISTORY };
}

export function openPovisioningConfirmDialog() {
    return { type: AppDialogActionType.OPEN_PROV_CONFIRM_DIALOG };
}

export function openSRNotesDetailsDialog() {
    return { type: AppDialogActionType.OPEN_SR_NOTES_DETAILS_DIALOG };
}

export function openTaskUpdateDialogRequest() {
    return { type: AppDialogActionType.OPEN_TASK_UPDATE_REQUEST };
}

export function openUWQFilterDialogRequest() {
    return { type: AppDialogActionType.OPEN_UWQ_FILTER_REQUEST };
}
