import React, { Component } from 'react';
import { connect } from 'react-redux';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import Button from '@material-ui/core/Button';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import './login.style.css'; import Select from '@material-ui/core/Select';
import { fetchLoginData } from '../../actions/login.action';
import { withRouter } from 'react-router'
import { LocalStorageService } from '../../services/localstorage.service';
import { HashRouter as Router, Route, Redirect } from 'react-router-dom';
import { openSnackbarRequest } from '../../actions/snackbar.action';
import { withStyles } from '@material-ui/core/styles';
import styles from './login.style';
import newId from '../../services/util.service';
import {
    Grid, Typography, ExpansionPanelDetails, Table, TableHead, TableBody, TableRow, TableCell
} from '@material-ui/core';

class LoginForm extends Component {
    constructor(props) {
        super(props);
        this.state = { fname: '', lname: '', emailid: '', gender: '', selectedDepartment: '', id: '', userDataList: [], searchText: '' };
        this.onSubmitHandler = this.onSubmitHandler.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.sortBy = this.sortBy.bind(this);
        this.compareBy = this.compareBy.bind(this);
        this.onChangeFieldValue = this.onChangeFieldValue.bind(this);


    }
    componentWillMount() {
        this.id = newId();
    }
    componentDidMount() {
        this.fetchUserDataList()

    }
    fetchUserDataList = () => {
        let userdata = JSON.parse(localStorage.getItem('registationInfo'));
        if (userdata && userdata.length > 0) {
            this.setState({ userDataList: userdata });
        }
    };

    handleChangeDepartmentType = event => {
        this.setState({ selectedDepartment: event.target.value });
    };

    handleChange = prop => event => {
        this.setState({ [prop]: event.target.value });
    };
    onChangeFieldValue = (event, value) => {
        event.preventDefault();
        this.setState({ searchText: event.target.value });
        if (event.target.value) {
            let arrayCopy = [...this.state.userDataList];
            const data = arrayCopy.filter(function (item) {
                console.log(Object.values(item));
                return !item.string.includes(event.target.value);
            });
            console.log("arrayCopy", data);
            // this.setState({ userDataList: arrayCopy });
        }

    };
    resetData = () => {
        this.setState({
            fname: '',
            lname: '',
            emailid: '',
            gender: '',
            department: '',
        })
    };


    onSubmitHandler = (event) => {
        event.preventDefault();
        const myObj = {
            id: this.id,
            fname: this.state.fname,
            lname: this.state.lname,
            emailid: this.state.emailid,
            gender: this.state.gender,
            department: this.state.selectedDepartment
        }
        LocalStorageService.setRegistationInfo(myObj);
        this.resetData();
        this.fetchUserDataList();
    }
    compareBy(key) {
        debugger
        return function (a, b) {
            if (a[key] < b[key]) return -1;
            if (a[key] > b[key]) return 1;
            return 0;
        };
    }

    sortBy(event, key) {
        event.preventDefault();
        if (this.state.userDataList && this.state.userDataList.length > 0) {
            let arrayCopy = [...this.state.userDataList];
            arrayCopy.sort(this.compareBy(key));
            this.setState({ userDataList: arrayCopy });
        }



    }

    render() {

        const { classes } = this.props;
        const isEnabled = this.state.fname && this.state.gender && this.state.lname && this.state.selectedDepartment ? true : false;

        const departmentList = ['Engineering', 'Marketing', 'Finance', 'HR'];
        let userList = this.state.userDataList || [];
        console.log("searchText", this.state.searchText);

        return (
            <div>

                <form onSubmit={this.onSubmitHandler}>
                    <FormControl fullWidth>
                        <TextField
                            label="fname"
                            placeholder="Enter first name"
                            margin="normal"
                            type="text"
                            value={this.state.fname}
                            onChange={this.handleChange('fname')}
                        />
                    </FormControl>
                    <FormControl fullWidth>
                        <TextField
                            label="lname"
                            placeholder="Enter Last Name"
                            margin="normal"
                            type="text"
                            value={this.state.lname}
                            onChange={this.handleChange('lname')}
                        />
                    </FormControl>
                    <FormControl fullWidth>
                        <TextField
                            label="emailid"
                            placeholder="Enter EmailId"
                            margin="normal"
                            type="email"
                            value={this.state.emailid}
                            onChange={this.handleChange('emailid')}
                        />
                    </FormControl>
                    <FormControl fullWidth>
                        <TextField
                            label="gender"
                            placeholder="Enter gender"
                            margin="normal"
                            type="text"
                            value={this.state.gender}
                            onChange={this.handleChange('gender')}
                        />
                    </FormControl>
                    <FormControl fullWidth className={classes.formControl}>
                        <InputLabel htmlFor="channel-simple">Department</InputLabel>
                        <Select
                            value={this.state.selectedDepartment}
                            onChange={this.handleChangeDepartmentType}
                            input={<Input name="selectedDepartment" id="channel-simple" />}
                        >

                            <MenuItem value="">Department Type</MenuItem>
                            {
                                departmentList.map(type => {
                                    return (
                                        <MenuItem key={type} value={type}>{type}</MenuItem>
                                    )
                                })
                            }
                        </Select>
                    </FormControl>


                    <FormControl className="pull-right loginButton">

                        <Button type="submit" value="Submit" variant="contained" size="large"
                            className="color-white"
                            disabled={!isEnabled}
                            onClick={event => this.onSubmitHandler(event)}>
                            register</Button>
                    </FormControl>
                </form>

                <Grid container spacing={32}>
                    <Grid item xs={12}>
                        <Grid container>
                            <Grid item xs={12}>
                                <Table>
                                    <TableHead>
                                        <TableRow className="th-height">
                                            <TableCell className="table-cell-padding"><Typography component="h4"><strong>Index</strong></Typography></TableCell>
                                            <TableCell

                                                className="table-cell-padding"><Typography component="h4"><strong>First Name</strong>
                                                    <Button type="submit" value="sorting" variant="contained" size="large"

                                                        onClick={event => this.sortBy(event, 'fname')}>
                                                        sort</Button>
                                                </Typography></TableCell>
                                            <TableCell className="table-cell-padding"><Typography component="h4"><strong>Last name</strong></Typography></TableCell>
                                            <TableCell className="table-cell-padding"><Typography component="h4"><strong>Email</strong> </Typography></TableCell>
                                            <TableCell className="table-cell-padding"><Typography component="h4"><strong>Gender</strong> </Typography></TableCell>
                                            <TableCell className="table-cell-padding"><Typography component="h4"><strong>Department</strong> </Typography></TableCell>
                                        </TableRow>
                                    </TableHead>

                                    <TableBody>
