const styles = theme => ({
  button: {
    margin: 4,
    background: '#03A9F4',
    padding: 5,
    color: '#FFF',
    width: 130,
    fontWeight: 'bold',
    '&:hover': {
      background: '#03A9F4'
    }
  },
  input: {
    display: 'none',
  },
  textField: {
    padding: "0px",
    margin: '5px 0px 0px 10px',
    width: 'calc(100% - 150px)',
    border: '1px solid #ccc',
    borderRadius: 8,
    height: 36,
    '& div': {
      height: 'inherit'
    },
    '& fieldset': {
      border: 'none'
    },
    '& input': {
      fontSize: 14
    }
  },
  backBtn: {
    textAlign: 'right',
    '& svg': {
      fontSize: 20,
      background: '#fff',
      border: '1px solid #03A9F4',
      color: '#03A9F4',
      borderRadius: 3,
      cursor: 'pointer'
    }
  },
  scenarioDesc: {
    // maxHeight: 225,
    // overflow: 'auto'
  }
});

export default styles;
