const styles = theme => ({
    button: {
        margin: theme.spacing.unit,
        padding: theme.spacing.unit * 1.5
    },
    root: {
        width: '100%',
    },
    tableHeading: {
        fontWeight: theme.typography.fontWeightRegular,
        fontSize: '13px !important',
        marginBottom: '5px'
    },
    row: {
        '&:nth-of-type(odd)': {
            backgroundColor: "#eee",
        },
        height: 'auto'
    },
    innerRow: {
        '&:nth-of-type(odd)': {
            backgroundColor: "#ccc",
        },
        height: 'auto'
    },
    cardLoader: {
        width: '50% !important',
        overflow: 'hidden',
        position: 'relative',
        display: 'block',

    },
    tagIcon: {
        top: '10px',
        position: 'absolute',
        margin: '0px 5px',
        textAlign: 'center',
        cursor: 'pointer',
        float: 'right',
        right: 40,
        width: 15,
    },
    customRowHeight: {
        height: '30px !important',
    },
    benefitsIcon: {
        maxHeight: '25px',
        verticalAlign: 'middle',
    }
});
export default styles;
