const styles = theme => ({
    root: {
        '& table': {
            border: '1px solid #ccc'
        }
    },
    searchBtn: {
        width: 100,
        height: 50
    },
    btnContainer: {
        margin: 20,
        textAlign: 'right'
    },
    heading: {
        fontSize: 18,
        fontWeight: 'bold',
    },
    expandIcon: {
        fontSize: 30
    },
    table: {
        border: 'none !important',
        '& thead th': {
            fontSize: 16
        },
        '& th, td': {
            borderBottom: '1px solid #ccc'
        },
        '& tr': {
            height: 40
        }
    },
    panelDetails: {
        padding: 0
    },
    openNetworkIcon: {
        position: 'absolute',
        top: 10,
        width: 20,
        left: 115
    },
    expandSummary: {
        minHeight: '50px !important'
    }
})

export default styles;
