export const submitArcIssueDialogData = () => (dispatch, getState) => {

    dispatch(showLoader());
    const state = getState();
    const authDetails = CookieManagerService.getAuthDetails();
    const agentId = authDetails && authDetails['agentId'] ? authDetails['agentId'] : null;
    const lob = authDetails && authDetails['lob'] ? authDetails['lob'] : null;
    const msisdn = CookieManagerService.getItem(AppConstant.MSISDN);
    const source = authDetails && authDetails['source'] ? authDetails['source'] : null;
    const transactionId = CookieManagerService.getItem(AppConstant.CORRELATION_ID) || "";
    const arcIssueSubmitDataObj = {
        'tagging': state.scenario.crmCode ? state.scenario.crmCode : null,
        'notes': state.arcIssueInfo.selectedArcNotes ? state.arcIssueInfo.selectedArcNotes : null,
        'scenarioName': state.arcIssueInfo.selectedArcIssueVoc ? state.arcIssueInfo.selectedArcIssueVoc : null,
        'customerMsisdn': msisdn,
        'agentId': agentId,
        'lob': lob,
        'source': source,
        'transactionId': transactionId,
        'ffc': state.arcIssueInfo.selectedArcIssueVocObject && state.arcIssueInfo.selectedArcIssueVocObject.ffc ? state.arcIssueInfo.selectedArcIssueVocObject.ffc : null,
        'rc': state.arcIssueInfo.selectedArcIssueVocObject && state.arcIssueInfo.selectedArcIssueVocObject.resolutionCode ? state.arcIssueInfo.selectedArcIssueVocObject.resolutionCode : null,
        'caseType': state.arcIssueInfo.selectedArcIssueVocObject && state.arcIssueInfo.selectedArcIssueVocObject.qrc ? state.arcIssueInfo.selectedArcIssueVocObject.qrc : null,
        'type': state.arcIssueInfo.selectedArcIssueType ? state.arcIssueInfo.selectedArcIssueType : null,
        'subType': state.arcIssueInfo.selectedArcIssueSubType ? state.arcIssueInfo.selectedArcIssueSubType : null,
        'storeCROId': state.arcIssueInfo.selectedArcStoreId ? state.arcIssueInfo.selectedArcStoreId : null
    }
    dispatch(submitArcIssueApiLoading());
    ArcissueDialogService.submitArcIssueApi(arcIssueSubmitDataObj).then(
        response => {
            dispatch(hideLoader());
            if (response && response.data && response.data.result) {
                dispatch(submitArcIssueApiSuccess(response.data.result));
                if (response.data.result && response.data.result.tagId) {
                    message = SnackbarMessages.ARC_ISSUE_SUCCESS_MSG + " " + response['data']['result']['tagId'];
                    dispatch(diaplayArcSuccessMessageDialog());
                } else {
                    message = SnackbarMessages.DEFAULT_ARC_ISSUE_SUCCESS_MSG
                }
                // dispatch(openSnackbarRequest(null, message));

                dispatch(submitArcEmailHandler());
            }
        },
        error => {
            dispatch(hideLoader());
            if (error) {
                dispatch(openSnackbarRequest(error));
            }
            else {
                message = SnackbarMessages.DEFAULT_ERROR_MSG;
                dispatch(openSnackbarRequest(null, message));
            }
            dispatch(submitArcIssueApiFailure(error));
        }
    );
}

