    if (errMessage && errMessage !== '') {
        dispatch(hideLoader());
        dispatch(openSnackbarRequest(null, errMessage));
        return false;
    }
    else {
        dispatch(showLoader());
        const authDetails = CookieManagerService.getAuthDetails();
        const agentId = authDetails && authDetails['agentId'] ? authDetails['agentId'] : null;
        const msisdn = CookieManagerService.getItem(AppConstant.MSISDN);
