            'circle': state.arcIssueInfo.selectedArcIssueCircle ? state.arcIssueInfo.selectedArcIssueCircle : null,
            'scenarioName': state.arcIssueInfo.selectedArcIssueVoc ? state.arcIssueInfo.selectedArcIssueVoc : null,
