            'location': state.arcIssueInfo.selectedArcIssueLocation ? state.arcIssueInfo.selectedArcIssueLocation : null,
            'address': state.arcIssueInfo.selectedArcIssueStoreNameAddr ? state.arcIssueInfo.selectedArcIssueStoreNameAddr : null,
            'storeType': state.arcIssueInfo.selectedArcIssueStoreType ? state.arcIssueInfo.selectedArcIssueStoreType : null,
            'arcSubType': state.arcIssueInfo.selectedArcIssueSubType ? state.arcIssueInfo.selectedArcIssueSubType : null,
            'arcType': state.arcIssueInfo.selectedArcIssueType ? state.arcIssueInfo.selectedArcIssueType : null,
            'agentId': agentId,
            'notes': state.arcIssueInfo.selectedArcNotes ? state.arcIssueInfo.selectedArcNotes : null,
            'mailTo': state.arcIssueInfo.selectedArcIssueStoreObject && state.arcIssueInfo.selectedArcIssueStoreObject.emailTo ? state.arcIssueInfo.selectedArcIssueStoreObject.emailTo : null,
            'mailCC': state.arcIssueInfo.selectedArcIssueStoreObject && state.arcIssueInfo.selectedArcIssueStoreObject.emailCc ? state.arcIssueInfo.selectedArcIssueStoreObject.emailCc : null,
            'source': source,
            'channel': state.arcIssueInfo.selectedArcIssueChannel ? state.arcIssueInfo.selectedArcIssueChannel : null,
            'storeCROID': state.arcIssueInfo.selectedArcStoreId ? state.arcIssueInfo.selectedArcStoreId : null
        };
        dispatch(submitArcEmailHandleApiLoading());
        ArcissueDialogService.submitArcEmailHandleApi(arcIssueSubmitEmailObj).then(
            response => {
                dispatch(hideLoader());
                if (response && response.data && response.data.result) {
                    message = response.data.result;
                }
                else {
                    message = SnackbarMessages.DEFAULT_ARC_EMAIL_SUCCESS_MSG;
                }
                // dispatch(openSnackbarRequest(null, message));
                dispatch(submitArcEmailHandleApiSuccess(response));
            },
            error => {
                dispatch(hideLoader());
                if (error) {
                    dispatch(openSnackbarRequest(error));
                }
                else {
                    message = SnackbarMessages.DEFAULT_ERROR_MSG;
                    dispatch(openSnackbarRequest(null, message));
                }
                dispatch(submitArcEmailHandleApiFailure(error));
            }
        );
    }


}

function submitArcEmailHandleApiLoading() { return { type: ArcIssueDialogActionType.SUBMIT_ARC_EMAIL_REQUEST } }
function submitArcEmailHandleApiSuccess(response) { return { type: ArcIssueDialogActionType.SUBMIT_ARC_EMAIL_SUCCESS, payload: response } }
function submitArcEmailHandleApiFailure(error) { return { type: ArcIssueDialogActionType.SUBMIT_ARC_EMAIL_FAILURE, payload: error } }

