import CustomHttpService from './custom-http.service';
import Config from '../config/config';
import CookieManagerService from './cookie.service';
import { AppConstant } from '../constants/app.constant';


import EncodeDecodeType from '../actiontype/encode-decode-type';

class ArcissueDialogService {

    static encodeForGet(str) {
        if (str) {
            return encodeURIComponent(str);
        }
        return "";
    };

    //fetch arc issue channels
    static fetchArcIssueChannelRequest() {
        const config = {
            headers: {
            }
        };
        const authDetails = CookieManagerService.getAuthDetails();
        const lob = authDetails && authDetails['lob'] ? authDetails['lob'] : null;
        //"/user/rest/v1.0/scenario/lob/channels/{lobName}"
        var url = Config.contextPath + "/user/rest/v1.0/scenario/lob/channels/" + lob;
        return CustomHttpService.get(url, config, EncodeDecodeType.DEFAULT);
    }

    //fetch arc issue circle
    static fetchArcIssueCircleRequest() {
        const config = {
            headers: {
            }
        };
        const authDetails = CookieManagerService.getAuthDetails();
        const agentId = authDetails && authDetails['agentId'] ? authDetails['agentId'] : null;
        const transactionId = CookieManagerService.getItem(AppConstant.CORRELATION_ID) || "";
        var url = Config.contextPath + "/user/rest/v1.0/arc/stores/circles?agentId=" + agentId + "&transactionId=" + transactionId;
        return CustomHttpService.get(url, config, EncodeDecodeType.DEFAULT);
    }

    //fetch arc issue type
    static fetchArcIssueTypeRequest() {
        const config = {
            headers: {
            }
        };
        const authDetails = CookieManagerService.getAuthDetails();
        const agentId = authDetails && authDetails['agentId'] ? authDetails['agentId'] : null;
        const transactionId = CookieManagerService.getItem(AppConstant.CORRELATION_ID) || "";
        var url = Config.contextPath + "user/rest/v1.0/arc/scenario/types?agentId=" + agentId + "&transactionId=" + transactionId;
        return CustomHttpService.get(url, config, EncodeDecodeType.DEFAULT);
    }

    //fetch arc issue sub type
    static fetchArcIssueSubTypeRequest(arcIssueType) {
        const config = {
            headers: {
            }
        };
        const authDetails = CookieManagerService.getAuthDetails();
        const agentId = authDetails && authDetails['agentId'] ? authDetails['agentId'] : null;
        const transactionId = CookieManagerService.getItem(AppConstant.CORRELATION_ID) || "";
        var url = Config.contextPath + 'user/rest/v1.0/arc/scenario/subTypes?agentId=' + agentId + '&transactionId=' + transactionId + '&type=' + this.encodeForGet(arcIssueType);
        return CustomHttpService.get(url, config, EncodeDecodeType.DEFAULT);
    }

    //fetch arc issue sub type
    static fetchArcIssueVocRequest(arcIssueType, arcIssueSubType) {
        const config = {
            headers: {
            }
        };
        const authDetails = CookieManagerService.getAuthDetails();
        const agentId = authDetails && authDetails['agentId'] ? authDetails['agentId'] : null;
        const transactionId = CookieManagerService.getItem(AppConstant.CORRELATION_ID) || "";
        var url = Config.contextPath + 'user/rest/v1.0/arc/scenario?agentId=' + agentId + '&transactionId=' + transactionId + '&type=' + arcIssueType + '&subType=' + arcIssueSubType;
        return CustomHttpService.get(url, config, EncodeDecodeType.DEFAULT);
    }


    //fetch arc issue CITY 
    static fetchArcIssueCityRequest(selectedArcIssueCircle) {
        const config = {
            headers: {
            }
        };
        const authDetails = CookieManagerService.getAuthDetails();
        const agentId = authDetails && authDetails['agentId'] ? authDetails['agentId'] : null;
        const transactionId = CookieManagerService.getItem(AppConstant.CORRELATION_ID) || "";
        var url = Config.contextPath + 'user/rest/v1.0/arc/stores/circle/cities?agentId=' + agentId + '&transactionId=' + transactionId + '&circle=' + selectedArcIssueCircle;
        return CustomHttpService.get(url, config, EncodeDecodeType.DEFAULT);
    }

    //fetch arc issue LOCATION
    static fetchArcIssueLocationRequest(selectedArcIssueCity, selectedArcIssueCircle) {
        const config = {
            headers: {
            }
        };
        const authDetails = CookieManagerService.getAuthDetails();
        const agentId = authDetails && authDetails['agentId'] ? authDetails['agentId'] : null;
        const transactionId = CookieManagerService.getItem(AppConstant.CORRELATION_ID) || "";
        var url = Config.contextPath + 'user/rest/v1.0/arc/stores/circle/city/locations?agentId=' + agentId + '&transactionId=' + transactionId + '&circle=' + selectedArcIssueCircle + '&city=' + selectedArcIssueCity;
        return CustomHttpService.get(url, config, EncodeDecodeType.DEFAULT);
    }

    //fetch arc issue store name and address
    static fetchArcIssueSoreNameAddrRequest(selectedArcIssueCircle, selectedArcIssueCity, selectedArcLocation) {
        const config = {
            headers: {
            }
        };
        const authDetails = CookieManagerService.getAuthDetails();
        const agentId = authDetails && authDetails['agentId'] ? authDetails['agentId'] : null;
        const transactionId = CookieManagerService.getItem(AppConstant.CORRELATION_ID) || "";
        var url = Config.contextPath + 'user/rest/v1.0/arc/stores/?agentId=' + agentId + '&transactionId=' + transactionId + '&circle=' + selectedArcIssueCircle + '&city=' + selectedArcIssueCity + '&location=' + selectedArcLocation;
        return CustomHttpService.get(url, config, EncodeDecodeType.DEFAULT);
    }
    //fetch arc issue store type
    static fetchArcIssueStoreType(selectedArcIssueCircle, selectedArcIssueCity, selectedArcLocation, selectedStoreNameAddr) {
        const config = {
            headers: {
            }
        };
        const authDetails = CookieManagerService.getAuthDetails();
        const agentId = authDetails && authDetails['agentId'] ? authDetails['agentId'] : null;
        const transactionId = CookieManagerService.getItem(AppConstant.CORRELATION_ID) || "";
        var url = Config.contextPath + 'user/rest/v1.0/arc/stores/types/?agentId=' + agentId + '&transactionId=' + transactionId + '&circle=' + this.encodeForGet(selectedArcIssueCircle) + '&city=' + this.encodeForGet(selectedArcIssueCity) + '&location=' + this.encodeForGet(selectedArcLocation) + '&address=' + this.encodeForGet(selectedStoreNameAddr);
        return CustomHttpService.get(url, config, EncodeDecodeType.DEFAULT);
    }

    //submit arc issue dialog object data
    static submitArcIssueApi(arcIssueSubmitObj) {
        const config = {
            headers: {
            }
        };
        const authDetails = CookieManagerService.getAuthDetails();
        const lob = authDetails && authDetails['lob'] ? authDetails['lob'] : null;

        var url = Config.contextPath + "user/rest/v1.0/tag/" + lob + "/siebel/arc";
        return CustomHttpService.post(url, arcIssueSubmitObj, config, EncodeDecodeType.DEFAULT);
    }

    //submit arc issue email handler
    static submitArcEmailHandleApi(arcIssueSubmitEmailObj) {
        const config = {
            headers: {
            }
        };
        const authDetails = CookieManagerService.getAuthDetails();
        const agentId = authDetails && authDetails['agentId'] ? authDetails['agentId'] : null;
        const transactionId = CookieManagerService.getItem(AppConstant.CORRELATION_ID) || "";
        var url = Config.contextPath + "user/rest/v1.0/arc/mail/?agentId=" + agentId + "&transactionId=" + transactionId;
        return CustomHttpService.post(url, arcIssueSubmitEmailObj, config, EncodeDecodeType.DEFAULT);
    }

    static handleVocSelectedIndex(arraytosearch, valuetosearch) {
        for (var i = 0; i < arraytosearch.length; i++) {
            if (arraytosearch[i]['scenarioName'] === valuetosearch) {
                return i;
            }
        }
        return null;
    }
    static handleStoreNameAddrSelectedIndex(arraytosearch, valuetosearch) {
        for (var i = 0; i < arraytosearch.length; i++) {
            if (arraytosearch[i]['storeAddress'] === valuetosearch) {
                return i;
            }
        }
        return null;
    }

}

export default ArcissueDialogService;
