import { LsiPingStatusActionType } from '../actiontype/lsi-ping-status.actiontype';
import CommonActionType from '../actiontype/common.actiontype';
import { CustomerInfoActionType } from '../actiontype/customer-info.actiontype';

const initialState = {
    LsiPingStatusTabStatus: '',
    LsiPingStatusApiResponse: null,
    LsiPingStatusApiLinkStatus: null,
    LsiPingStatusTabLoader: false

};

export default function LsiPingStatusApiReducer(state = initialState, action) {

    switch (action.type) {
        case LsiPingStatusActionType.LSI_PING_STATUS_REQUEST:
            return {
                ...state,
                LsiPingStatusTabStatus: "LOADING"
            };
        case LsiPingStatusActionType.LSI_PING_STATUS_SUCCESS:
            return {
                ...state,
                LsiPingStatusTabStatus: true,
                LsiPingStatusApiResponse: action.pingStatus,
                LsiPingStatusApiLinkStatus: action.linkStatus
            };
        case LsiPingStatusActionType.LSI_PING_STATUS_FAILURE:
            return {
                ...state,
                LsiPingStatusTabStatus: false
            };
        case LsiPingStatusActionType.UPDATE_LSI_PING_STATUS_VALUE:
            return {
                ...state,
                LsiPingStatusTabStatus: true,
                LsiPingStatusApiResponse: action.payload
            };
        case LsiPingStatusActionType.UPDATE_LSI_LINK_STATUS_VALUE:
            return {
                ...state,
                LsiPingStatusTabStatus: true,
                LsiPingStatusApiLinkStatus: action.payload
            };
        case LsiPingStatusActionType.SHOW_LOADER:
            return {
                ...state,
                LsiPingStatusTabLoader: true,
            };
        case LsiPingStatusActionType.HIDE_LOADER:
            return {
                ...state,
                LsiPingStatusTabLoader: false,
            };
        case CustomerInfoActionType.RESET_CUSTOMER:
        case CommonActionType.RESET_STATE:
            return {
                ...state,
                ...initialState
            }
        default:
            return state
    }

}
