import React, { Component } from 'react';
import CustomizedTabs from '../../common/customized-tabs/customized-tabs.dumb';
import { withStyles } from '@material-ui/core/styles';
import PropTypes from 'prop-types';
import { switchScenarioTab, setScenarioTab } from '../../../actions/tabs.action';
import { connect } from 'react-redux';
import styles from './scenario-tabs.style';

const tabOptions = {
    // scrollable: false,
}

class ScenarioTabs extends Component {

    handleChange = (event) => {
        const id = event.currentTarget.id;
        this.props.switchScenarioTab(id);
    };

    getTabContent(key) {
        const { scenarioTabList } = this.props;
        if (key) {
            const tabObj = scenarioTabList[key];
            const obj = {
                index: Object.keys(scenarioTabList).indexOf(key),
                content: tabObj.component
            }
            return obj;
        }
        return {};
    }

    render() {
        const { classes, scenarioActiveTab, scenarioTabList, markedChoiceColor } = this.props;
        const { index, content } = this.getTabContent(scenarioActiveTab);
        const tabContentHeight = '300px'
        return (
            <div className={classes.root + " system-check-wrapper mb-10"}>
                 <CustomizedTabs value={index} tabOptions={tabOptions} handleChange={this.handleChange.bind(this)} tabList={scenarioTabList} content={content} markedChoiceColor={markedChoiceColor} tabContentHeight={tabContentHeight}></CustomizedTabs>
            </div>
        )
    }
}

ScenarioTabs.propTypes = {
    classes: PropTypes.object.isRequired,
};

const mapStateToProps = state => {
    return {
        scenarioActiveTab: state.tab.scenarioActiveTab,
        scenarioTabList: state.tab.scenarioTabList,
        markedChoiceColor: state.scenario.markedChoiceColor
    };
};

const mapDispatchToProps = dispatch => ({
    switchScenarioTab: index => dispatch(switchScenarioTab(index)),
    setScenarioTab: data => dispatch(setScenarioTab(data))
});

export default connect(mapStateToProps, mapDispatchToProps)(withStyles(styles)(ScenarioTabs));
