import SearchSVG from '../../../assets/images/search.svg';

const styles = theme => ({
    container: {
        position: 'relative',
    },
    suggestionsContainerOpen: {
        position: 'absolute',
        marginTop: theme.spacing.unit,
        zIndex: 10,
        left: 0,
        right: 0,
        maxHeight: 300,
        overflow: 'auto'
    },
    suggestion: {
        display: 'block',
    },
    suggestionsList: {
        margin: 0,
        padding: 0,
        listStyleType: 'none',
    },
    divider: {
        height: theme.spacing.unit * 2,
    },
    search: {
        background: 'url(' + SearchSVG + ') no-repeat',
        border: 'none',
        width: 25,
        height: 25,
        cursor: 'pointer',
        position: 'absolute',
        top: 15,
        right: 22,
        '&.disable': {
            opacity: '0.5',
            cursor: 'not-allowed'
        }
    },
    clear: {
        cursor: 'pointer',
        position: 'absolute',
        top: 20,
        right: 22,
        fontSize: 20
    }
});

export default styles;
